<?phpclass Admin_Model_Order extends Zend_Db_Table_Abstract{	protected $_name = 'order';		public function getAllOrder(){        $sql = $this->_db->select()                         ->from($this->_name)                         ->where('1=1')                         ->order('id desc');                    return $this->_db->fetchAll($sql);    }	public function getOrder($id){        $sql = $this->_db->select()                         ->from($this->_name)                         ->where('id='.$id);            return $this->_db->fetchRow($sql);       }	public function addOrder($order_id,$cus_mail,$cus_type,$grandTotal,$payment_method,$status){		$arrdata = array(            'order_id' 		=> $order_id,            'cus_email'  	=> $cus_mail,			'cus_type'  	=> $cus_type,			'grand_total'  	=> $grandTotal,			'payment_method'=> $payment_method,			'status'		=> $status        );		return $this->insert($arrdata);	}		public function editOrder($cus_type,$grandTotal,$payment_method,$status,$cond){        $arrdata = array(            			'cus_type'  	=> $cus_type,			'grand_total'  	=> $grandTotal,			'payment_method'=> $payment_method,			'status'		=> $status        );        return $this->update($arrdata,'id='.$cond);    }}